INSERT INTO `products` (`product_id`, `product_cat`, `product_brand`, `product_title`, `product_price`, `product_qty`, `product_desc`, `product_image`) VALUES
(1, 2, 2, 'Intel Celeron J4125', 20000, 5, 'Мини ПК DEXP MINI ENTRY – компактный неттоп, имеющий предустановленную операционную систему Windows 10 Pro. Она позволит приступить к работе за компьютером сразу после его установки и подключения, не тратя время на поиск и закачку нужного софта. 4-ядерный процессор Intel Celeron J4125 с тактовой частотой 2 ГГц обеспечивает стабильную работу системы без подвисаний, подтормаживаний и слишком длительной загрузки приложений. Один из двух слотов для ОЗУ занят плашкой оперативной памяти объемом 8 ГБ. Для вывода изображения на экран используется встроенный графический чипсет Intel UHD Graphics 600.\r\nМини ПК DEXP MINI ENTRY располагает накопителем M.2 SATA, имеющим емкость 128 ГБ для долговременного хранения данных. Подключить к компьютеру монитор можно при помощи разъема HDMI, а для разнообразной периферии предусмотрены интерфейсы USB на задней панели. Сетевой адаптер обеспечит стабильное проводное Интернет-соединение со скоростью обработки данных до 1 Гбит/с.\r\n4 x 2 ГГц, 8 ГБ DDR4, SSD 128 ГБ, Windows 11 Pro', 'pk_one.jpg'),
(2, 2, 2, 'Intel Pentium Gold G6405', 20499, 7, 'ПК DEXP Aquilon O298 в черном-корпусе Mid-Tower подходит для решения повседневных задач. 2-ядерный процессор с 4 потоками и ОЗУ 8 ГБ не зависает при одновременном запуске нескольких приложений. Поставка без ОС позволяет выбирать привычную версию системы с оптимальным набором программ.\r\nDEXP Aquilon O298 оснащен скоростным SSD-накопителем с 256 ГБ свободной памяти. На корпусе спереди есть 2 USB-порта и 2 аудиоразъема для быстрого доступа. Набор интерфейсов на задней панели упрощает подключение контроллеров, мониторов, акустических систем и прочих периферийных устройств. Встроенный сетевой адаптер гарантирует стабильную передачу данных со скоростью до 1 Гбит/сек.', 'pk_two.jpg'),
(4, 2, 3, 'Intel Pentium Gold G6405', 21199, 10, 'ПК DEXP Atlas H417 в черном корпусе стандартного формата обладает необходимой для эффективной работы или учебы мощностью. В зависимости от выполняемых задач на ПК можно установить нужную операционную систему. Процессор Intel Pentium Gold имеет 2 ядра, которые выполняют вычисления с тактовой частотой 4 – 4.1 ГГц. Это предполагает комфортную работу с офисными приложениями и браузером. Оперативная память на 8 ГБ позволяет работать в многозадачном режиме без торможения и зависания программ. Возможностей встроенной видеокарты достаточно для воспроизведения видео в высоком качестве.\r\nВ ПК DEXP Atlas H417 установлен SSD-накопитель объемом 256 ГБ, что обеспечивает хранение и быстрый запуск всех файлов. Для подключения к ПК мониторов предусмотрены видеоразъемы HDMI и VGA (D-Sub). На передней панели корпуса расположены USB порты для периферийных устройств. Клавиатуру и мышь можно подсоединять через разъем PS/2. Сетевой адаптер поддерживает стабильное интернет-соединение на скорости 1 Гбит/с.\r\n2 x 4.1 ГГц, 8 ГБ DDR4, SSD 256 ГБ, без ОС.', 'pk_three.jpg'),
(5, 2, 3, 'Intel Pentium Gold G6405', 21899, 13, 'ПК DEXP Atlas H411 в компактном черном корпусе Mini-Tower обладает производительностью, которой достаточно для эффективной офисной работы, учебы или развлечений. Он оснащен многопоточным 2-ядерным процессором Intel Pentium Gold G6405 с тактовой частотой 4.1 ГГц, что исключает задержки при одновременном выполнении множества вычислительных операций. Комфортную работу в нескольких программах, быстрое переключение между несколькими вкладками в браузере обеспечивает модуль оперативной памяти объемом 8 ГБ.\r\nНаличие в ПК DEXP Atlas H411 твердотельного накопителя вместимостью 256 ГБ предусматривает хранение и быструю загрузку системных и программных файлов. Подключить к ПК мышь, клавиатуру, колонки и другую периферию можно через порты USB, PS/2 (комбинированный), аудиоразъемы. Для мониторов предусмотрены видеоразъемы HDMI, VGA (D-Sub). Стабильное интернет-соединение на скорости 1 Гбит/с обеспечивает встроенный сетевой адаптер.\r\n2 x 4.1 ГГц, 8 ГБ DDR4, SSD 256 ГБ, без ОС.', 'pk_free.jpg'),
(10, 2, 1, 'AMD Ryzen 5 5600G', 39999, 6, 'ПК DEXP Mars G001 выполнен в компактном форм-факторе Mini-Tower и несмотря на это оборудован мощными компонентами, которые делают его интересным вариантом для геймеров. Платформа данной модели представлена процессором AMD Ryzen 5 5600G, 16 ГБ системной памяти, а также вместительным хранилищем объемом 512 ГБ на основе накопителя SSD. Передняя панель привлекает внимание перфорированной отделкой. На верхней части DEXP Mars G001 расположена панель с кнопкой включения, портами USB 2.0 и USB 3.2 Gen 1. Для подключения источников изображения предусмотрены 3 видеовыхода – HDMI VGA и DVI-D. Соединение с сетью Интернет осуществляется за счет использования интерфейса Ethernet 1 Гбит/с.\r\n6 x 3.9 ГГц, 16 ГБ DDR4, SSD 512 ГБ, без ОС.', 'pk_fife.jpg'),
(11, 2, 1, 'Intel Core i3-12100F', 50299, 3, 'ПК ARDOR GAMING NEO M141 – игровой компьютер в корпусе Mid-Tower классического черного цвета. Модель поставляется без предустановленной операционной системы, чтобы каждый пользователь смог сделать выбор в пользу привычного и нужного в повседневном использовании программного обеспечения. Мощная сборка представлена 4-ядерным процессором Intel Core i3-12100F со способностью обрабатывать до 8 потоков информации и 16 ГБ оперативной памяти. Такая конфигурация обеспечивает стабильную работу системы при решении любых задач.\r\nИгровой компьютер ARDOR GAMING NEO M141 стал обладателем дискретной видеокарты NVIDIA GeForce GTX 1650 с 4 ГБ видеопамяти для моментального построения и отображения на экране монитора ресурсоемкой графики. Долговременное хранение важных данных можно доверить SSD-накопителю емкостью 500 ГБ. Подключение к сети Интернет обеспечивает порт LAN со скоростью обработки информации до 1 Гбит/с. Боковая панель предусматривает прозрачное окошко, открывающее вид на подсветку компонентов системы.\r\n4 x 3.3 ГГц, 16 ГБ DDR4, GeForce GTX 1650, SSD 500 ГБ, без ОС.', 'pk_sex.jpg'),
(19, 2, 1, 'Intel Core i3-12100F', 52299, 4, 'ПК ARDOR GAMING NEO M144 представляет собой игровую сборку на базе 4-ядерного процессора Intel Core i3-12100F. Функционирующий с тактовой частотой 3.3 ГГц чипсет способен одновременно обрабатывать до 8 вычислительных потоков информации, благодаря чему система сможет быстро запускать ресурсоемкие программы и стабильно работать в режиме многозадачности. Повышению производительности сборки также способствует 16 ГБ оперативной памяти DDR4-типа. Для большинства пользователей отсутствие предустановленной операционной системы будет плюсом. Владелец компьютера сможет сделать выбор в пользу привычной ОС и установить на него нужный в повседневном использовании софт.\r\nПК ARDOR GAMING NEO M144 предусматривает дискретную видеокарту NVIDIA GeForce GTX 1650 с 4 ГБ объема видеопамяти. Она обеспечит моментальную обработку и вывод на экран реалистичной игровой графики. Для долговременного хранения данных в сборке присутствует SSD-накопитель емкостью 500 ГБ.\r\n', 'pk_seven.jpg'),
(20, 2, 2, 'Мини ПК Rombica Horizon J4 GKJ442D', 21499, 19, 'Intel Celeron J4125, 4 x 2 ГГц, 4 ГБ LPDDR4, SSD 256 ГБ, без ОС.\r\nМини-ПК Rombica Horizon J4 GKJ442D гармонично впишется в пространство офиса или квартиры благодаря лаконичному дизайну и классическому черному цвету. Встроенный вентилятор работает бесшумно, эффективно охлаждая при этом все компоненты ПК. Это обеспечивает его стабильную и производительную работу. 4-ядерная структура процессора Celeron J4125 отлично справляется с рабочей нагрузкой, позволяя быстро выполнять заданные действия. Отсутствие предустановленной ОС дает возможность модифицировать ПК под конкретные задачи.\r\nДостойную производительность мини-ПК Rombica Horizon J4 GKJ442D обеспечивают 4 ГБ интегрированной оперативной памяти. Для быстрой загрузки файлов и их хранения предусмотрен SSD-накопитель объемом 256 ГБ. Стабильное и высокоскоростное подключение к интернету поддерживает проводной LAN-порт (1 Гбит/с), а также модуль Wi-Fi 5. Для синхронизации мини-ПК с периферийными устройствами используются разъемы USB и Bluetooth. Есть кард-ридер microSD (TF).', 'pk_eight.jpg'),
(21, 2, 1, 'ПК ARDOR GAMING NEO M137', 63999, 4, 'ПК ARDOR GAMING NEO M137 в черном корпусе формата Mid-Tower с прозрачным боковым окном обеспечит эффективную работу с графикой и производительность в играх. Для этого он оснащен 6-ядерным процессором Intel Core i5, который производит вычисления в 12-поточном режиме и повышает тактовую частоту при нагрузке до 4.4 ГГц. Это исключает задержки и торможения системы, программ и игр. Плавность и четкость игровых текстур и локаций поддерживают 2 модуля оперативной памяти на 16 ГБ. Ускоряет запуск системных и программных файлов SSD-накопитель вместимостью 500 ГБ.\r\nВ ПК ARDOR GAMING NEO M137 установлена дискретная видеокарта GeForce RTX 3050 с выделенной памятью 8 ГБ, что предусматривает четкость и реалистичность изображения. Видеоразъемы HDMI, DisplayPort и DVI-I позволяют подключать до 3 мониторов с поддержкой разрешения 4К и 8К. Для стабильного интернет-соединения используется гигабитный сетевой адаптер. Порты USB Type-A и разъем PS/2 обеспечат надежное подключение к ПК периферийных устройств.\r\nIntel Core i5-12400F, 6 x 2.5 ГГц, 16 ГБ DDR4, GeForce RTX 3050, SSD 500 ГБ, без ОС.', 'pk_game_one.jpg'),
(22, 2, 1, 'ПК ARDOR GAMING NEO M136', 29999, 7, 'ПК ARDOR GAMING NEO M136 подходит для запуска игр и подобных мощных программ. 4-ядерный процессор работает в 8-поточном режиме и исключает малейшие зависания. 2 слота ОЗУ на 16 ГБ сохраняют высокую производительность, плавную смену локаций и динамичных сцен в играх. Дискретная видеокарта GeForce на 4 ГБ поддерживает изображение в 4K-формате.\r\nARDOR GAMING NEO M136 оснащен скоростным SSD-накопителем с 512 ГБ свободной памяти. Отсутствие установленной ОС позволяет выбирать удобную версию для работы и игр. Набор интерфейсов упрощает подключение любых периферийных устройств. LAN-порт поддерживает передачу данных со скоростью 1 Гбит/сек.\r\nIntel Core i3-12100F, 4 x 3.3 ГГц, 16 ГБ DDR4, GeForce GTX 1650, SSD 512 ГБ, без ОС.', 'pk_game_two.jpg'),
(23, 2, 3, 'Мини ПК ASUS ExpertCenter', 30499, 9, 'Мини-ПК ASUS ExpertCenter PN42-SN068AV черного цвета удобен для работы и учебы. Возможность подключать к нему до 3 дисплеев одновременно предусматривает его использование при создании цифровых вывесок. Он оснащен 4-ядерным процессором от Intel, с тактовой частотой 1–3.7 ГГц, что обеспечивает быстрое выполнение всех операций. 4 ГБ оперативной памяти позволяет одновременно работать в нескольких программах или приложениях и легко переключаться между ними.\r\nДля хранения и быстрого запуска всех файлов в мини-ПК ASUS ExpertCenter PN42-SN068AV используется твердотельный накопитель на 128 ГБ. ОС Windows 11 Pro удобна своим расширенным функционалом для решения корпоративных задач. Встроенный сетевой адаптер на 2.5 Гбит/с и модуль Wi-Fi 6 обеспечат стабильное и высокоскоростное интернет-подключение. 7 USB-портов позволят синхронизировать мини-ПК со всеми нужными устройствами. Пассивная система охлаждения предполагает бесшумную работу компьютера и исключает скопление пыли внутри корпуса.\r\nIntel Processor N200, 1 ГГц, 4 ГБ DDR4, SSD 128 ГБ, Windows 11 Pro.', 'pk_home_one.jpg'),
(24, 2, 3, 'Мини ПК ASUS ExpertCenter', 60499, 3, 'Мини ПК ASUS ExpertCenter PN64-S5435AD – компьютер в корпусе-неттопе, производительность которого ни в чем не уступает полноразмерному ПК. На борту устройства присутствует предустановленная операционная система Windows 11 Pro, благодаря которой приступить к работе за компьютером можно сразу после его распаковки и подключения. 12-ядерный процессор Intel Core i5-12500H способен одновременно обрабатывать до 16 вычислительных потоков, что вместе с 8 ГБ оперативной памяти обеспечит системе быстродействие для работы в многозадачном режиме.\r\nМини ПК ASUS ExpertCenter PN64-S5435AD оснащен накопителем данных, представленным SSD-диском емкостью 256 ГБ. На фронтальной панели имеются порты USB Type-A и Type-C для быстрого и удобного подключения внешних жестких дисков и флешек. Два видеоразъема HDMI позволят подсоединить к компьютеру два монитора для более комфортной работы с большими объемами данных. Для подключения к Интернету используется встроенный сетевой адаптер со скоростью обработки данных до 2.5 Гбит/с.\r\nIntel Core i5-12500H, 4 x 2.5 ГГц, 8 ГБ DDR5, SSD 256 ГБ, Windows 11 Pro.', 'pk_home_two.jpg'),
(25, 2, 2, 'Мини ПК DEXP MINI SMART B002', 31999, 4, 'Мини ПК DEXP MINI SMART B002 с процессором AMD Ryzen 7 3750H не так прост, как кажется. В его маленькой «коробочке» скрывается производительная начинка, которой достаточно для выполнения любых офисных задач и развлечений. Интегрированная видеокарта AMD Radeon RX Vega 10 сочетается с 16 ГБ оперативной памяти. Для хранения документов и файлов у него есть твердотельный накопитель на 512 ГБ. За счет этого решения DEXP MINI SMART B002 не теряет производительности даже при многозадачной нагрузке. Благодаря креплению VESA расположить неттоп получится даже на стене, спрятав его за монитором.\r\nAMD Ryzen 7 3750H, 4 x 2.3 ГГц, 16 ГБ DDR4, SSD 512 ГБ, Windows 11 Pro.', 'pk_biz_one.jpg'),
(26, 2, 2, 'Мини ПК Lenovo ThinkCentre', 62999, 5, 'Lenovo ThinkCentre M70q Gen 3 — это мощный корпоративный ПК в корпусе объемом 1 л, обеспечивающий идеальный баланс между производительностью и занимаемой площадью. Высокопроизводительный процессор Intel Core 12-го поколения, быстрая память DDR4 и возможности настройки системы хранения данных позволят вам решать любые задачи. Подключите ПК к модульному монитору Tiny-in-One, чтобы создать собственный моноблок.\r\nIntel Core i3-12100T, 4 x 2.2 ГГц, 8 ГБ DDR4, SSD 256 ГБ, Windows 11 Pro.', 'pk_biz_two.jpg'),
(35, 3, 1, 'Acer Predator Helios 300 PH317-51-7717', 76999, 5, 'Игровой Ноутбук с диагональю экрана 17.3 дюймов и разрешением Full HD. Оснащен процессором Intel Core i7 7700HQ с тактовой частотой 2800 МГц. Объем оперативной памяти составляет 8192 МБ (8 ГБ), а жесткий диск имеет емкость 1000 ГБ (1 ТБ). Видеокарта — GeForce GTX 1050 Ti с объемом памяти 4096 МБ (4 ГБ). Ноутбук поддерживает беспроводные технологии Wi-Fi и Bluetooth, оснащен встроенной камерой. Установлена операционная система Windows 10 Home (64 бит). Цвет корпуса — черный.', 'lp_game.jpg');

